import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';
import Color from "./Color";
import {Button} from "@material-ui/core";

function App() {
    const [spin, setSpin] = useState(0);

    return (
        <div className="App">
            <Color spin={spin} style={{background: 'red', left: '20%', top: '20%', transform: 'translateX(-50%)'}}/>
            <Color spin={spin} style={{background: 'blue', left: '20%', bottom: '20%', transform: 'translateX(-50%) translateY(-50%)'}}/>
            <Button onClick={() => {
                setSpin(spin + 360)
            }}>Spin</Button>
        </div>
    );
}

export default App;
