import React, {useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';
import Color from "./Color";
import {Button} from "@material-ui/core";
import TextField from '@material-ui/core/TextField';

function App() {
    const [spin, setSpin] = useState(false);
    const [color1, setColor1] = useState('');
    const [color2, setColor2] = useState('');
    const [color3, setColor3] = useState('');
    const [sumaRojo, setSumaRojo] = useState(0);
    const [sumaVerde, setSumaVerde] = useState(0);
    const [sumaAzul, setSumaAzul] = useState(0);

    useEffect(() => {
        const rojo1 = '0x' + color1.substr(0, 2);
        const rojo2 = '0x' + color2.substr(0, 2);
        const rojo3 = '0x' + color3.substr(0, 2);

        const azul1 = '0x' + color1.substr(2, 2);
        const azul2 = '0x' + color2.substr(2, 2);
        const azul3 = '0x' + color3.substr(2, 2);

        const verde1 = '0x' + color1.substr(4, 2);
        const verde2 = '0x' + color2.substr(4, 2);
        const verde3 = '0x' + color3.substr(4, 2);

        setSumaRojo((rojo1 + rojo2 + rojo3) / 3)
        setSumaAzul((azul1 + azul2 + azul3) / 3)
        setSumaVerde((verde1 + verde2 + verde3) / 3)

        console.log(rojo1, rojo2, rojo3)
        console.log(azul1, azul2, azul3)
        console.log(verde1, verde2, verde3)
        console.log(sumaRojo, sumaVerde, sumaAzul)

    });


    return (
        <div className="App">
            <Color spin={spin} style={{background: 'red', left: '50%', top: '0%', transform: 'translateX(-50%)'}}/>
            <Color spin={spin} style={{background: 'green', right: '0%', bottom: '0%'}}/>
            <Color spin={spin} style={{background: 'blue', left: '0%', bottom: '0%'}}/>

            <Button onClick={() => {
                setSpin(!spin)
            }}>Spin</Button>


            <TextField onChange={(e) => {setColor1(e.target.value)}}  id="standard-basic" label="Standard" />
            <TextField onChange={(e) => {setColor2(e.target.value)}}  id="standard-basic" label="Standard" />
            <TextField onChange={(e) => {setColor3(e.target.value)}}  id="standard-basic" label="Standard" />



            <div style={{ width:50, height: 50, background: '#' + color1}}></div>
            <div style={{ width:50, height: 50, background: '#' + color2}}></div>
            <div style={{ width:50, height: 50, background: '#' + color3}}></div>
            <div style={{ width:50, height: 50, background: `rgb(${sumaRojo}, ${sumaVerde},${sumaAzul},)`}}></div>

        </div>
    );
}

export default App;
