import React, {useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';
import Color from "./Color";
import {Button} from "@material-ui/core";
import TextField from '@material-ui/core/TextField';
import Glass from "./Glass";

function App() {
    const [spin, setSpin] = useState(false);
    const [color1, setColor1] = useState('ff0000');
    const [color2, setColor2] = useState('00ff00');
    const [color3, setColor3] = useState('0000ff');

    let timer;

    const [suma, setSuma] = useState('');


    const [color1Loader, setColor1Loader] = useState(0);
    const [color2Loader, setColor2Loader] = useState(0);
    const [color3Loader, setColor3Loader] = useState(0);

    function load(){
        if (color1Loader <= 79)
            setColor1Loader(color1Loader+1);
        else clearTimeout(timer);

        if (color2Loader <= 79)
            setColor2Loader(color2Loader+1);
        else clearTimeout(timer);

        if (color3Loader <= 79)
            setColor3Loader(color3Loader+1);
        else clearTimeout(timer);
    }

    useEffect(() => {
        timer = setTimeout(() => load(), 100)

        return () => {
            clearTimeout(timer)
        }
    })

    useEffect(() => {
        const rojo1 = parseInt('0x' + color1.substr(0, 2));
        const rojo2 = parseInt('0x' + color2.substr(0, 2));
        const rojo3 = parseInt('0x' + color3.substr(0, 2));

        const azul1 = parseInt('0x' + color1.substr(4, 2));
        const azul2 = parseInt('0x' + color2.substr(4, 2));
        const azul3 = parseInt('0x' + color3.substr(4, 2));

        const verde1 = parseInt('0x' + color1.substr(2, 2));
        const verde2 = parseInt('0x' + color2.substr(2, 2));
        const verde3 = parseInt('0x' + color3.substr(2, 2));

        const rojo = rojo1 + rojo2 + rojo3
        const azul = azul1 + azul2 + azul3
        const verde = verde1 + verde2 + verde3

        setSuma(`rgb(${rojo}, ${verde}, ${azul})`);

        // console.log(`rgb(${rojo}, ${verde}, ${azul})`)
        //
        // console.log(rojo1, rojo2, rojo3)
        // console.log(azul1, azul2, azul3)
        // console.log(verde1, verde2, verde3)
    });


    return (
        <div className="App">
            {/*<Color spin={spin} style={{background: 'red', left: '50%', top: '0%', transform: 'translateX(-50%)'}}/>*/}
            {/*<Color spin={spin} style={{background: 'green', right: '0%', bottom: '0%'}}/>*/}
            {/*<Color spin={spin} style={{background: 'blue', left: '0%', bottom: '0%'}}/>*/}

            <Glass percentage={color1Loader} style={{ position: 'absolute', top: 0, left: 0 }}/>
            <Glass percentage={color2Loader} style={{ position: 'absolute', top: 0, right: 0 , transform: 'translateX(100%)'}}/>
            <Glass percentage={color3Loader} style={{ position: 'absolute', top: 0, left: '50%', transform: 'translateX(-50%)' }}/>

            <Button onClick={() => {
                setSpin(!spin)
            }}>Spin</Button>


            <TextField value={color1} onChange={(e) => {setColor1(e.target.value)}}  id="standard-basic" label="Standard" />
            <TextField value={color2} onChange={(e) => {setColor2(e.target.value)}}  id="standard-basic" label="Standard" />
            <TextField value={color3} onChange={(e) => {setColor3(e.target.value)}}  id="standard-basic" label="Standard" />

            <div style={{ width:50, height: 50, background: '#' + color1}}></div>
            <div style={{ width:50, height: 50, background: '#' + color2}}></div>
            <div style={{ width:50, height: 50, background: '#' + color3}}></div>
            <div style={{ width:50, height: 50, background: suma}}></div>

        </div>
    );
}

export default App;
